    m = -float('inf')
    mi = None
    for i in range(len(slen)):
        if slen[i] % 2 == 1 and slen[i] > m:
            mi = i
            m = slen[i]
    return mi

def find_even(slen, mi):
    for i in range(len(slen)):
        if i != mi and slen[i] > slen[mi] // 2:
            return False
    return True
