def find_two_max(bin):
    (max1, max2) = (0, 0)
    counter = 0
    for index in range(len(bin)):
        if bin[index] == '0':
            counter += 1
        else:
            if max1 < counter:
                max2 = max1
                max1 = counter
            elif max2 < counter:
                max2 = counter
            counter = 0
    if counter > 0:
        if max1 == max2:
            max1 = counter
        elif max1 < counter:
            max2 = max1
            max1 = counter
        elif max2 < counter:
            max2 = counter
    return (max1, max2)

def is_good(bin):
    (max1, max2) = find_two_max(bin)
    if max1 % 2 == 0:
        return False
    if max2 >= max1 // 2 + 1 and max2 != 0:
        return False
    return True

