def shot(s):
    yield from s

def score_round(s):
    return int(s == '1')

def solve(*args):
    (n, s) = args
    score = [0, 0]
    result = shot(s)
    for i in range(1, n + 1):
        for turn in [0, 1]:
            score[turn] += score_round(next(result))
            if score[0] + (n - i) < score[1] or score[1] + (n - i + 1 - turn) < score[0]:
                return 2 * i + turn - 1
    return 2 * n

