def chef(s):
    string = list(s)
    length = len(string)
    if length < 4:
        for i in range(length):
            if string[i] == '?':
                string[i] = 'A'
        return ''.join(string)
    pos = length - 1
    while pos >= 3:
        if (string[pos] == '?' or string[pos] == 'F') and (string[pos - 1] == '?' or string[pos - 1] == 'E') and (string[pos - 2] == '?' or string[pos - 2] == 'H') and (string[pos - 3] == '?' or string[pos - 3] == 'C'):
            string[pos] = 'F'
            string[pos - 1] = 'E'
            string[pos - 2] = 'H'
