class Solution:

    def solve(self):
        self.solve_each()

    def solve_each(self):
        s = input()
        n = len(s)
        count = 0
        search_s = 'CHEF'
        search_dic = dict(((c, idx) for (idx, c) in enumerate(search_s)))
        dp = dict()
        for c in s:
            idx = search_dic[c]
            if not idx:
                dp[idx] = dp.get(idx, 0) + 1
                continue
            if dp.get(idx - 1, 0):
                dp[idx] = dp.get(idx, 0) + 1
                dp[idx - 1] -= 1
        print(dp.get(3, 0))

def main():
    sol = Solution()
    sol.solve()
