def bin_search(el, lst):
    if lst == []:
        return -1
    if lst[0] > el:
        return -1
    else:
        low = 0
        high = len(lst) - 1
        while low <= high:
            mid = (low + high) // 2
            if el < lst[mid]:
                high = mid - 1
            else:
                low = mid
            if high - low == 1:
                if lst[high] < el:
                    return high
                return low
            if low == high:
                return low
        return low
