class Solu(object):

    def solve(self, N, H, D):
        if N == 1:
            return max(H[0] - D[0], -1)
        elif N == 2:
            a = H[0] - D[0]
            b = H[1] - D[1]
            if a == 0 and b == 0:
                return 0
            elif a != b and abs(a) == abs(b):
                return abs(a)
            else:
                return -1
        else:
            d = sum(D)
            h = sum(H)
            if d < h:
                return -1
            r = (d - h) % (N - 2)
            if r != 0:
                return -1
            s = (d - h) / (N - 2)
            for i in range(N):
                r = (s + H[i] - D[i]) % 2
                if r != 0:
                    return -1
                xi = 0.5 * (s + H[i] - D[i])
                if xi < 0:
                    return -1
            return int(s)
