while True:
    N = input()
    if N == '':
        continue
    elif int(N) == 0:
        break
    min_users = 0
    for n in range(int(N)):
        (po, score) = input().split()
        score = int(score)
        if po == 'P':
            score -= 1
        else:
            score += 1
        current_count = abs(score)
        if current_count > min_users:
            min_users = current_count
        elif (min_users - current_count) % 2:
            min_users += 1
    print(min_users)
