def exp(a, n):
    if n == 1:
        return digi(a)
    if n == 0:
        return 1
    elif n % 2 == 1:
        return exp(exp(a, 1) * exp(a, n - 1), 1)
    else:
        temp = exp(a, n // 2)
        return exp(temp * temp, 1)

