for _ in range(int(input())):
    n = int(input())
    l = [int(i) for i in input().split()]
    xor = 0
    for i in l:
        xor = xor ^ i
    mini = 100000000000
    for i in l:
        temp = i ^ xor
        if abs(temp - i) < mini:
            mini = abs(temp - i)
    print(mini)

for _ in range(int(input())):
    n = int(input())
    arr = list(map(int, input().split()))
    xor_value = arr[0]
    for i in range(1, n):
        xor_value = xor_value ^ arr[i]
    mini = xor_value
    for j in range(n):
        ans = xor_value ^ arr[j]
        if mini > abs(ans - arr[j]):
            mini = abs(ans - arr[j])
    print(mini)
