t = int(input())
for i in range(t):
    (n, m) = map(int, input().split())
    c = n // m
    s = n % m
    if m == 2:
        print((s + c) * (c + s - 1) // 2 + c * (c - 1) // 2)
    elif m % 2 == 0:
        print(c * c * ((m - 1) // 2) + (c - 1) * c + c * s + max(0, s - m // 2))
    else:
        print(c * c * ((m - 1) // 2) + (c - 1) * c // 2 + c * s + max(0, s - m // 2))

