for _ in range(int(input())):
    val = list(map(int, input().split()))
    val.pop(0)
    thing = input()
    req = int(input())
    sol = [''] * 501
    for i in range(0, len(thing)):
        th = val[ord(thing[i]) - ord('a')]
        for m in range(req - th, -1, -1):
            if sol[m] == '':
                if m != 0:
                    continue
            thi = sol[m] + thing[i]
            if sol[m + th] == '':
                sol[m + th] = thi
            elif thi < sol[m + th]:
                sol[m + th] = thi
    if sol[req] == '':
        print('IMPOSSIBLE')
    else:
        print(sol[req])

