    sum = d0 + d1
    k -= 2
    if k > 0:
        sum += sum % 10
        k -= 1
    if k > 0:
        patternSum = 2 * (d0 + d1) % 10 + 4 * (d0 + d1) % 10 + 8 * (d0 + d1) % 10 + 6 * (d0 + d1) % 10
        sum += k // 4 * patternSum
        rem = k % 4
        if rem == 1:
            sum += 2 * (d0 + d1) % 10
        elif rem == 2:
            sum += 2 * (d0 + d1) % 10 + 4 * (d0 + d1) % 10
        elif rem == 3:
            sum += 2 * (d0 + d1) % 10 + 4 * (d0 + d1) % 10 + 8 * (d0 + d1) % 10
    if sum % 3 == 0:
        print('YES')
    else:
        print('NO')
