def helper(n):
    if n % 4 == 0:
        return n * 5
    elif n % 4 == 1:
        return (n - 1) * 5 + 8
    elif n % 4 == 2:
        return (n - 2) * 5 + 14
    elif n % 4 == 3:
        return (n - 3) * 5 + 16

def funcx(k, d0, d1):
    tsum = d0 + d1
    if k < 7:
        i = 2
        while i < k:
            tsum += tsum % 10
            i += 1
        if tsum % 3 == 0:
            return True
        return False
    digit = tsum % 10
    if digit == 5 or digit == 0:
        return False
    else:
        if digit == 1 or digit == 6:
            tsum += digit + 6 + helper(k - 5)
        elif digit == 2 or digit == 7:
            tsum += digit + 4 + helper(k - 4)
        elif digit == 8:
            tsum += helper(k - 2)
        elif digit == 3:
            tsum += digit + 12 + helper(k - 6)
        elif digit == 4 or digit == 9:
            tsum += digit + helper(k - 3)
        if tsum % 3 == 0:
            return True
        return False
