        l = s + bar[0] + sum(bar[1:]) * q + sum(bar[1:r + 1])
    print('YES' if l % 3 == 0 else 'NO')

t = int(input())
for z in range(0, t, 1):
    (n, d1, d2) = map(int, input().split())
