def solve(A):
    n = len(A)
    if n % 2 != 0:
        return None
    for i in range(n // 2):
        (a, b) = (A[i], A[i + n // 2])
        if a > 0 and b > 0 and (a != b):
            return None
        if a < 0 and b < 0:
            a = 1
        elif a < 0:
            a = b
        (A[i], A[i + n // 2]) = (a, a)
    return A
