def factorize(y):
    z = list()
    x = 2
    while x * x <= y:
        if y % x == 0:
            z += [x]
            while y % x == 0:
                y //= x
        x += 1
    if y > 1:
        z += [y]
    return z

def CoprimeCount(fact, x):
    k = len(fact)
    Net = 1 << k
    ans = 0
    for mask in range(Net):
        cnt = 0
        val = 1
        for i in range(k):
            if 1 << i & mask != 0:
                val *= fact[i]
                cnt += 1
        if cnt & 1 == 1:
            ans -= x // val
        else:
            ans += x // val
    return ans
test = int(input().strip())
while test > 0:
    test -= 1
    (y, x1, x2) = [int(_) for _ in input().strip().split(' ')]
    y = abs(y)
    if y == 1:
        if x1 * x2 <= 0:
            ans = abs(x1) + abs(x2) + 1
        else:
