    fact = factorize(y)
    ans = 0
    if x1 * x2 < 0:
        ans += CoprimeCount(fact, abs(x1))
        ans += CoprimeCount(fact, abs(x2))
    else:
        if x1 <= 0 and x2 <= 0:
            (x1, x2) = (-x2, -x1)
        ans += CoprimeCount(fact, x2)
        ans -= CoprimeCount(fact, max(0, x1 - 1))
    print(ans)

