import math

def Network(n, m):
    upperLimit = n * (n + 1) // 2
    lowerLimit = n - 1
    if (n == 2 or n == 1) and m == 1:
        return 1
    if n == 1 and m == 0:
        return 0
    if m > upperLimit or m < lowerLimit:
        return -1
    elif m <= n + 1:
        return 2
    elif m <= 2 * n:
        return 3
    else:
        x = math.ceil(m / n)
        if m % n <= n // 2 and m % n != 0:
            return 2 * x - 2
        else:
            return 2 * x - 1
