def net(n, m):
    val4 = int(n / 2)
    valu4 = math.ceil(n / 2)
    subTotal = 2 * n + val4
    loop = int(n * (n + 1) / 2)
    mcp = m - subTotal
    if m <= n - 2 or m > loop:
        print(-1)
    elif n <= 2 and m == 1:
        print(1)
    elif m == 0:
        print(0)
    elif n - 1 <= m <= n + 1:
        print(2)
    elif n + 2 <= m <= 2 * n:
        print(3)
    elif 2 * n + 1 <= m <= 2 * n + val4:
        print(4)
    else:
        startNum = 5
        updateNum = math.ceil(mcp / n)
        if 1 <= mcp % n <= valu4:
            print(startNum + (updateNum - 1) * 2)
        else:
            print(startNum + 1 + (updateNum - 1) * 2)
