letters = ['a', 'b', 'c', 'd', 'e', 'f', 'g', 'h', 'i', 'j', 'k', 'l', 'm', 'n', 'o', 'p', 'q', 'r', 's', 't', 'u', 'v', 'w', 'x', 'y', 'z']
indices = {letter: letters.index(letter) for letter in letters}
for _ in range(int(input())):
    count = [0 for i in range(26)]
    ans = 0
    arr = list(input())
    for let in arr[::-1]:
        if let == 'z':
            count[-1] += 1
            continue
        else:
            count[indices[let]] += 1
            ans += sum(count[indices[let] + 1:])
    fin = ans
    tobeinc = []
    for let in letters[:-1]:
        if count[indices[let]] != 0:
            tobeinc += [arr.index(let)]
    for index in tobeinc:
        current = ans
        countl = [0 for i in range(26)]
        countr = [0 for i in range(26)]
        for i in range(index):
            countl[indices[arr[i]]] += 1
        for i in range(index + 1, len(arr)):
            countr[indices[arr[i]]] += 1
        current = current - sum(countl[:indices[arr[index]]]) - sum(countr[indices[arr[index]] + 1:])
        for i in range(indices[arr[index]] + 1, 26):
            new = current + sum(countl[:i]) + sum(countr[i + 1:]) + abs(indices[arr[index]] - i)
            fin = min(fin, new)
    tobedic = []
    for let in letters[1:]:
        if count[indices[let]] != 0:
            tobedic += [len(arr) - 1 - arr[::-1].index(let)]
    for index in tobedic:
