        current = current - sum(countl[:indices[arr[index]]]) - sum(countr[indices[arr[index]] + 1:])
        for i in range(0, indices[arr[index]]):
            new = current + sum(countl[:i]) + sum(countr[i + 1:]) + abs(indices[arr[index]] - i)
            fin = min(fin, new)
    print(fin)

