T = int(input())

def check(mid, n, oth_ls, chef_ls):
    sum = 0
    for i in range(n):
        if oth_ls[i] > mid:
            sum += chef_ls[i]
            if chef_ls[i] > mid:
                return False
    return sum <= 2 * mid
for t in range(T):
    n = int(input())
    (oth_ls, chef_ls) = ([], [])
    for _ in range(n):
        (oth, chef) = map(int, input().split())
        oth_ls.append(oth)
        chef_ls.append(chef)
    (start, end) = (1, 1000)
    ans = -1
    while start <= end:
        mid = (start + end) // 2
        if check(mid, n, oth_ls, chef_ls):
            ans = mid
            end = mid - 1
        else:
            start = mid + 1
    print(ans)

