t = int(input())
for i in range(t):
    (a, b, o) = map(int, input().split())
    r = 10 ** 2 + 5
    (d, s) = ([0] * 10 ** 5, [0] * r)
    for j in range(r):
        for k in range(r):
            if j == k or k == 0:
                d[j * r + k] = 1
            else:
                d[j * r + k] = d[(j - 1) * r + k - 1] + d[(j - 1) * r + k]
    for j in range(b + 1):
        ans = pow(a + 1, j + 1) - 1
        for k in range(j):
            ans -= d[(j + 1) * r + k] * s[k]
        s[j] = ans // (j + 1)
    print(s[b] % o)

