def good_contiguous_subsequence_length(numbers):
    repeat_count = 0
    repeating_indices = set()
    n = len(numbers)
    for index in range(n - 1):
        if index not in repeating_indices or index + 1 not in repeating_indices:
            if numbers[index] == numbers[index + 1]:
                repeat_count += 1
                repeating_indices.add(index)
                repeating_indices.add(index + 1)
    repeat_count = len(repeating_indices)
    if repeat_count == 0:
        return n
    elif repeat_count == n:
        return 1
    else:
        breakpoints = set()
        for value in repeating_indices:
            if value - 1 not in repeating_indices or value + 1 not in repeating_indices:
                breakpoints.add(value)
        breakpoints.add(0)
        breakpoints.add(n - 1)
        if numbers[-1] == numbers[-2]:
            breakpoints.remove(n - 1)
        if numbers[0] == numbers[1]:
            breakpoints.remove(0)
        breakpoints = sorted(breakpoints)
        if len(breakpoints) > 1:
            max_good_length = breakpoints[1] - breakpoints[0] + 1
            for point in range(1, len(breakpoints) - 1):
                if numbers[point] != numbers[point + 1]:
                    max_good_length = max(max_good_length, breakpoints[point + 1] - breakpoints[point] + 1)
        elif breakpoints == 1:
            max_good_length = min(n, 3)
        else:
            max_good_length = 1
        return max_good_length

def good_subsequence_length(numbers):
    max_subsequence = []
    for value in numbers:
        if len(max_subsequence) < 1 or value != max_subsequence[-1]:
            max_subsequence.append(value)
    return len(max_subsequence)
