        tbf = r - l - 1
        if k <= 1 and tbf != 0:
            return False
        for i in range(l + 1, n):
            if (i - l) % 2 != 0:
                arr[i] = 1 if arr[l] != 1 else 2
            else:
                arr[i] = arr[l]
        return True
    elif l == -1:
        tbf = r
        if k <= 1 and tbf != 0:
            return False
        for i in range(r - 1, -1, -1):
            if (r - i) % 2 != 0:
                arr[i] = 1 if arr[r] != 1 else 2
            else:
                arr[i] = arr[r]
        return True
    else:
        if r == l + 1 and arr[l] == arr[r]:
            return False
        if arr[l] == arr[r]:
            tbf = r - l - 1
            if tbf != 0 and k == 1:
                return False
            arr[l + 1] = 1 if arr[l] != 1 else 2
            return func(arr, l + 1, r, k)
