                return False
            for i in range(l + 1, r):
                arr[i] = arr[l] if (i - l) % 2 == 0 else arr[r]
            if arr[r - 1] == arr[r]:
                for x in [1, 2, 3]:
                    if arr[l] != x and arr[r] != x:
                        arr[r - 1] = x
                        break
            return True
