def isValid(arr):
    for i in range(1, len(arr)):
        if arr[i] == arr[i - 1] or arr[i] == -1:
            return False
    return True

def solve2(arr, n):
    (p1, p2) = ([i % 2 + 1 for i in range(n)], [2 - i % 2 for i in range(n)])
    for i in range(n):
        if arr[i] != p1[i] and arr[i] != -1:
            for i in range(n):
                if arr[i] != p2[i] and arr[i] != -1:
                    return 'NO'
            return 'YES\n' + ' '.join(list(map(str, p2)))
    return 'YES\n' + ' '.join(list(map(str, p1)))

def solve(arr, n, k):
    arr = [0] + arr + [0]
    for i in range(1, n + 1):
        if arr[i] == -1:
            for j in range(1, min(3, k) + 1):
                if arr[i - 1] != j and arr[i + 1] != j:
                    arr[i] = j
                    break
    if isValid(arr[1:-1]):
        return 'YES\n' + ' '.join(list(map(str, arr[1:-1])))
    return 'NO'

