def brackets(N, intervals):
    sequence = ['-' for _ in range(N)]
    intervals.sort(key=lambda x: x[1])
    b = ['(', ')']
    for interval in intervals:
        (l, r) = interval
        p = 0
        for i in range(l, r + 1):
            if sequence[i] == '-':
                sequence[i] = b[p]
                p = 1 - p
    return ''.join([s if s != '-' else '(' for s in sequence])
for _ in range(int(input())):
