def f(n, k):
    if n == 1:
        if k == 1:
            return 1
        else:
            return 0
    mod = 10 ** 9 + 7
    a = pow(k * n, n - 2, mod)
    b = pow(k * n - k, (k - 1) * n, mod)
    return a * b % mod
    return (k * n) ** (n - 2) * (k * n - k) ** ((k - 1) * n)

def main():
    (n, k) = tuple((int(x) for x in input().split()))
    print(f(n, k))
main()
