n = int(input())
ip = list(map(int, input().split()))
ip2 = []
for i in range(n - 1):
    if ip[i + 1] > ip[i]:
        ip2.append((ip[i], 'a'))
        ip2.append((ip[i + 1], 'b'))
    else:
        ip2.append((ip[i + 1], 'a'))
        ip2.append((ip[i], 'b'))
ip2 = sorted(ip2, key=lambda x: (x[0], x[1]))
m = 0
c = 0
l = 0
for i in range(len(ip2)):
    (a, b) = ip2[i]
    if a == l:
        if b == 'a':
            c += 1
        else:
            c -= 1
    else:
        if c > m:
            m = c
        if b == 'a':
            c += 1
        else:
            c -= 1
        l = a
print(m)

