def ot(n, a):
    i = 0
    j = n - 1
    while i <= j:
        if a[i] == 1:
            break
        if a[j] == 1:
            break
        i += 1
        j -= 1
    if i > j:
        return n
    total_s = sum(a) - 2 * i
    return total_s + i

