def sub_sums(arr):
    sums = [arr[0]]
    for i in range(1, len(arr)):
        sums.append(sums[-1] + arr[i])
    sums.append(arr[1])
    for i in range(2, len(arr)):
        sums.append(sums[-1] + arr[i])
    sums.append(arr[2])
    for i in range(3, len(arr)):
        sums.append(sums[-1] + arr[i])
    if arr[0] != 1 and 1 in arr:
        sums.append(1)
    return set(sums)

