def find_front_one(arr, n):
    count = 0
    for i in range(n):
        if arr[i] == 1:
            break
        count += 1
    return count

def find_back_one(arr, n):
    count = 0
    for i in range(n - 1, -1, -1):
        if arr[i] == 1:
            break
        count += 1
    return count
