def fun():
    test = int(input())
    ans = 0
    for t in range(test):
        (a, bc) = input().strip().split('+')
        (b, c) = bc.split('=')
        try:
            for base in range(16, 3, -1):
                if int(a, base) + int(b, base) == int(c, base):
                    ans += 1
                    break
        except:
            pass
    print(ans)
fun()

