T = int(input())
while T > 0:
    K = int(input())
    score = 0
    for i in range(K):
        line = [int(x) for x in input().split()]
        size = line[0]
        arr = line[1:]
        arr.sort()
        parity = arr[0] % 2
        num = 0
        check = False
        powerReduce = 0
        for j in arr:
            if j % 2 != parity:
                check = True
            if check:
                powerReduce = powerReduce + 1
            if j % 2 == 1:
                num = num + (1 << 45 - powerReduce)
            else:
                num = num - (1 << 45 - powerReduce)
        score = score + num
    if score > 0:
        print('ODD')
    elif score < 0:
        print('EVEN')
    else:
        print("DON'T PLAY")
    T = T - 1

