for _ in range(int(input())):
    (n, k) = map(int, input().split())
    l = list(map(int, input().split()))
    lis = []
    for i in range(len(l)):
        lis.append([l[i], i])
    lis.sort(reverse=True)
    (i, c1, m1, c2, m2, j) = (0, 0, 0, 0, 0, len(lis) - 1)
    while n != 0:
        if lis[j][0] < k:
            (c1, m1) = (lis[j][1], lis[j][0])
            lis[j][0] -= m1
            if lis[j][0] == 0:
                j -= 1
        else:
            (c1, m1) = (lis[j][1], k)
            lis[j][0] -= m1
            if lis[j][0] == 0:
                j -= 1
        if k - m1 < lis[i][0]:
            (m2, c2) = (k - m1, lis[i][1])
            lis[i][0] -= m2
            if lis[i][0] == 0:
                i += 1
        else:
            (m2, c2) = (lis[i][0], lis[i][1])
            lis[i][0] -= m2
            if lis[i][0] == 0:
                i += 1
        if lis[i][0] + lis[j][0] < k and i != j:
            lis[j + 1] = lis[i]
            i += 1
            j += 1
        print(c1, m1, c2, m2)
        n -= 1

