import io
import sys

def test_data():
    return io.StringIO('3\n4 0\n2 0\n2 1')

def get_input(source):
    with source as lines:
        t = int(next(lines))
        for _ in range(t):
            (n, b) = map(int, next(lines).split(' '))
            yield (n, b)

def calc(n, b):
    if n == 2 and b == 1:
        return [(1, 2)]
    if n < 3 or (b != 0 and n / b < 4):
        return None
    edges = []
    broom_length = b * 2 + 1
    for i in range(1, n):
        i += 1
        edges.append((i, i + 1 if i < broom_length else 1))
    return edges
inp = test_data() if '-test' in sys.argv else sys.stdin
for args in get_input(inp):
    edges = calc(*args)
    if edges == None:
        print('NO')
    else:
        print('YES')
        for e in edges:
            print('{} {}'.format(*e))

