for t in range(int(input())):
    (n, b) = map(int, input().split())
    if b == 0:
        if n == 2:
            print('NO')
            continue
        print('YES')
        for i in range(2, n + 1):
            print(1, i)
        continue
    for g in range(1, n):
        c = n - g
        ctra = [c // 2 + 1]
        if (c + 1) % 2 == 0:
            ctra.append(ctra[-1] + 1)
        l = c - 1
        r = g
        ctd = c
        ctda = []
        while l >= 0:
            if l <= n // 2 and r <= n // 2:
                ctda.append(ctd)
            ctd -= 1
            l -= 1
            r += 1
        m = None
        for x in ctra:
            for y in ctda:
                if abs(x - y) == b:
                    m = (g, x, y)
                    break
        if m is not None:
            break
    if m:
        print('YES')
        for i in range(2, n - m[0] + 1):
            print(i - 1, i)
        for i in range(n - m[0] + 1, n + 1):
            print(n - m[0], i)
    else:
        print('NO')
