A = [1]
k = 1
for n in range(2, 3328):
    if n & 1:
        k += 1
        A.append(A[-1] + A[-1] * (n - k) // k)
    else:
        A.append(A[-1] + A[-1] * k // (n - k))
Anums = set(A)
T = int(input())
for _ in range(T):
    cand = int(input())
    if cand & 1:
        print('NO')
    else:
        cand //= 2
        print('YES' if cand in Anums else 'NO')
