for _ in range(int(input())):
    n = int(input())
    arr = list(map(int, input().strip().split()))
    d = {}
    for i in range(n):
        if arr[i] not in d:
            d[arr[i]] = 1
        else:
            d[arr[i]] += 1
    ans = 0
    for A1 in d.keys():
        ans += d[A1] * (d[A1] - 1)
        for A2 in d.keys():
            if A1 != A2 and A1 | A2 <= max(A1, A2):
                ans += d[A1] * d[A2]
    print(ans // 2)

