from collections import Counter
import sys
readline = sys.stdin.readline
T = int(readline())
for t in range(T):
    N = int(readline())
    A = list(map(int, readline().split()))
    C = Counter(A)
    cnt = [0] * (max(A) + 1)
    for (a, c) in C.items():
        cnt[a] += c
    for i in range(20):
        for bit in range(max(A) + 1):
            if bit >> i & 1:
                cnt[bit] += cnt[bit ^ 1 << i]
    ans = 0
    for (a, c) in C.items():
        ans += cnt[a] * c
    for c in C.values():
        ans -= c * (c + 1) // 2
    print(ans)

