T = int(input())
for i in range(0, T):
    (K, M) = map(int, input().split())
    s = [int(x) for x in input().split()]
    arr = [0] * 32
    c = 0
    for j in range(0, len(s)):
        for k in range(0, len(arr)):
            if arr[k] == 0:
                arr[k] = s[j]
                s[j] = 0
            else:
                s[j] = min(s[j], s[j] ^ arr[k])
    for j in range(0, len(arr)):
        if arr[j] > 0:
            c = c + 1
    print(2 ** K // 2 ** c)

