import sys

def compute(n):
    res = {}
    for i in range(0, n + 1):
        res[i * (n + 1 - i)] = i
    return res

def output(n, k):
    for i in range(1, n + 1):
        if i == k:
            sys.stdout.write('1 ')
        else:
            sys.stdout.write('2 ')
    sys.stdout.write('\n')
    sys.stdout.flush()
ans = {}
tc = int(sys.stdin.readline().split()[0])
for i in range(tc):
    n = int(sys.stdin.readline().split()[0])
    if n not in ans:
        ans[n] = compute(n)
    odd = int(sys.stdin.readline().split()[1])
    if odd in ans[n]:
        output(n, ans[n][odd])
    else:
        print('-1')

