def ii():
    return int(input())

def mi():
    return map(int, input().split())

def li():
    return list(mi())

def check(A, n, k):
    num = 0
    dem = 0
    mx = 0
    AA = [0] * n
    (a, b) = (0, 0)
    for i in range(n):
        for j in range(n):
            if mx < (A[i] + A[j]) / (i + j + 2) and i != j:
                mx = (A[i] + A[j]) / (i + j + 2)
                a = i
                b = j
    num += A[a] + A[b]
    dem += a + b + 2
    AA[a] = 1
    AA[b] = 1
    x = 2
    while x < k:
        (a, b) = (0, 0)
        mx = 0
        for i in range(n):
            if AA[i] == 0:
                temp = (num + A[i]) / (dem + i + 1)
                if mx < temp:
                    mx = temp
                    a = A[i]
                    b = i + 1
        x += 1
        num += a
        dem += b
        AA[b - 1] = 1
    return mx
for _ in range(ii()):
    (n, k) = mi()
    A = li()
    if k == 1:
        print(max(A))
    else:
        for i in range(n):
            A[i] = A[i] * (i + 1)
        mx = check(A, n, k)
        print('{0:.10f}'.format(mx))

for _ in range(int(input())):
    (n, x) = [int(i) for i in input().split()]
    s = [int(i) for i in input().split()]
    cg = s[0]
    credits = 1
    for i in range(1, n):
        if s[i] > cg:
            cg = s[i]
            credits = i + 1
    s[credits - 1] = 0
    while x > 1:
        best = 0
        for i in range(n):
            if s[i] != 0:
                temp = (cg * credits + (i + 1) * s[i]) / (credits + 1 + i)
                if temp > best:
                    best = temp
                    val = i
        credits += val + 1
        cg = best
        s[val] = 0
        x -= 1
    print(cg)
