tenonehun = 1
for i in range(100):
    tenonehun = tenonehun * 10 % 1000000007
for tests in range(int(input())):
    [N, K] = [int(i) for i in input().split()]
    arange = {}
    for i in range(N):
        [X, D] = [int(i) for i in input().split()]
        try:
            arange[X * D] += 1
        except:
            arange[X * D] = 1
        try:
            arange[X * (D + 1)] -= 1
        except:
            arange[X * (D + 1)] = -1
    th = N - K
    cum = 0
    L = list(arange)
    L.sort()
    prev = 0
    As = 0
    for i in L:
        if cum >= th:
            As = (As + i - prev) % 1000000007
        cum += arange[i]
        prev = i
    if cum >= th:
        As = (As + tenonehun + 1 - prev) % 1000000007
    print(As)

