p = 10 ** 9 + 7
for i in range(int(input())):
    (l, r) = map(int, input().split())
    t = l + 1 & ~1
    if t >= r:
        print(0, r - l + 1)
        continue
    (K, m, C) = (1, 2, 0)
    while True:
        (t, m2) = (l, m << 1)
        if l & m:
            t = l + m2 & ~(m2 - 1)
        elif l & K:
            t = l | K
        if t + m > r:
            break
        (K, m) = (K << 1 | 1, m2)
    if K == 1:
        C = r - l + 1 - l % 2 >> 1 << 1
    else:
        (t, m2, m) = (l & ~K, m, m >> 1)
        while t | m <= r:
            s = t + m - 1
            if t >= l:
                C = (C + min(m, r - s) * m) % p
            t += m2
            if s >= l and s | m <= r:
                C = (C + min(m, s - l + 1) * m) % p
    print(K, C * 2 % p)
