def solve(A, k):
    n = len(A)
    A = sorted(A)
    B = [A[0]]
    AA = []
    for i in range(1, n):
        if A[i] == A[i - 1]:
            AA += [A[i]]
        else:
            B += [A[i]]
    if len(B) >= k:
        return -1
    k -= len(B)
    if k == 1:
        return -1
    if k == 2:
        for i in range(1, len(AA)):
            if AA[i] == AA[i - 1]:
                return -1
        return AA[0] * AA[1]
    AA = AA[k - 3:]
    if AA[1] == AA[2]:
        return AA[0] * AA[2]
    if AA[0] == AA[1]:
        return AA[1] * AA[2]
    ret = AA[1] * AA[2]
    for i in range(3, len(AA)):
        if AA[i] == AA[i - 1]:
            return min(ret, AA[0] * AA[i])
    return ret
